def _photo(name: str) -> str:
    # Busca nombre exacto, luego nombre sin sufijo " (Prime)" / " (Leyenda)"
    url = PLAYER_PHOTOS.get(name)
    if url:
        return url
    base = name.split(" (")[0]
    return PLAYER_PHOTOS.get(base, SILHOUETTE)

