# Post-procesado paso 1: aplica URLs verificadas de PLAYER_PHOTOS donde existan
_verified_urls = set(PLAYER_PHOTOS.values())
for _p in PLAYERS:
    _verified = PLAYER_PHOTOS.get(_p["name"]) or PLAYER_PHOTOS.get(_p["name"].split(" (")[0])
    if _verified:
        _p["photo"] = _verified

# Post-procesado paso 2: cualquier jugador sin URL verificada usa la silueta
for _p in PLAYERS:
    if _p["photo"] not in _verified_urls:
        _p["photo"] = SILHOUETTE


def get_players_by_rarity(rarity: str) -> list:
    return [p for p in PLAYERS if p["rarity"] == rarity]


def get_players_by_position(position: str) -> list:
    return [p for p in PLAYERS if p["position"] == position]


def get_player_by_id(player_id: int) -> Optional[dict]:
    for p in PLAYERS:
        if p["id"] == player_id:
            return p
    return None
